srcDoc.css:
#map {
  height: 500px;
}

/* DTU Custom Popup Styles */
.custom-popup .leaflet-popup-content-wrapper {
  background: #fff;
  border-radius: 8px;
  box-shadow: 0 3px 14px rgba(0, 0, 0, 0.2);
}

.custom-popup .leaflet-popup-content {
  margin: 0;
  padding: 0;
}

.popup-content {
  min-width: 200px;
}

.popup-title {
  background: #003C71;
  color: white;
  margin: 0;
  padding: 10px 15px;
  font-size: 16px;
  border-radius: 8px 8px 0 0;
}

.popup-body {
  padding: 15px;
  color: #333;
  font-size: 14px;
  line-height: 1.4;
}

.popup-footer {
  padding: 8px 15px;
  background: #f5f5f5;
  color: #666;
  font-size: 12px;
  border-radius: 0 0 8px 8px;
  text-align: right;
}

.custom-popup .leaflet-popup-tip {
  background: #003C71;
}

srcDoc.js:
appsmith.onReady(() => {
  function createVueInstance() {
    new Vue({
      el: "#app",
      data() {
        return {
          mapInstance: null,
          position: appsmith.model.map,
          locationsList: [{
            lat: 16.0464319,
            // Khoảng 3km về phía Nam
            lng: 108.2326082,
            title: "Địa điểm 1",
            content: "Cách 3km về phía Nam",
            iconClass: "fas fa-building",
            iconColor: "#FF0000"
          }, {
            lat: 16.0899319,
            // Khoảng 3km về phía Bắc-Đông
            lng: 108.2496082,
            title: "Địa điểm 2",
            content: "Cách 3km về phía Bắc-Đông",
            iconClass: "fas fa-university",
            iconColor: "#00FF00"
          }]
        };
      },
      mounted() {
        this.initMap();
        appsmith.onModelChange("map", () => {
          this.updatePosition();
        });
      },
      methods: {
        initMap() {
          this.mapInstance = new DTUAppsmithMap("map", {
            center: [this.position.lat, this.position.lng],
            zoom: 16
          });
          this.mapInstance.capNhatViTriHienTai(this.position.lat, this.position.lng, this.position.title, this.position.location);

          // Uncomment nếu cần tính toán khoảng cách và vẽ đường đi:
          // this.locationsList.forEach((location) => {
          //   const distance = this.mapInstance.tinhKhoangCachDenDiem(
          //     location.lat,
          //     location.lng
          //   );
          //   location.content = `Cách ${distance} km so với vị trí hiện tại`;
          //   this.mapInstance.veDuongDi(
          //     this.position.lat,
          //     this.position.lng,
          //     location.lat,
          //     location.lng
          //   );
          // });
        },
        updatePosition() {
          // Cập nhật vị trí hiện tại khi "map" thay đổi
          this.position = appsmith.model.map;
          this.mapInstance.capNhatViTriHienTai(this.position.lat, this.position.lng, this.position.title, this.position.location);
        }
      }
    });
  }
  createVueInstance(); // Tạo instance khi Appsmith sẵn sàng

  appsmith.onModelChange(() => {
    createVueInstance(); // Tạo lại instance khi model thay đổi
  });
});

