async () => {
  try {
    const [user] = await FindUser.run();
    var check = await objLogin.comparePassword(password.text, user.password);
    storeValue("id_admin", user.id);
    if (check) {
      user.token = await objLogin.createToken(user).then(async () => await UpdateToken.run(user));
      showAlert("Đăng nhập thành công", "success");
      navigateTo('MASTER_ADMIN');
    } else {
      showAlert("Email hoặc mật khẩu không đúng", "error");
    }
  } catch (error) {
    showAlert("Email hoặc mật khẩu không đúng", "error");
  }
}

