appsmith.onReady(() => {
  new Vue({
    el: "#app",
    data() {
      return {
        mapInstance: null,
        position: appsmith.model.location,
        // Vị trí hiện tại
        locationsList: appsmith.model.map // Danh sách các địa điểm
      };
    },
    async mounted() {
      this.initMap();
      this.hienThiDanhSachDiaChi();
    },
    methods: {
      // Hàm khởi tạo bản đồ
      initMap() {
        this.mapInstance = new DTUAppsmithMap("map", {
          center: [this.position.lat, this.position.lng],
          zoom: 16
        });
        this.mapInstance.capNhatViTriHienTai(this.position.lat, this.position.lng);

        // Duyệt qua từng địa điểm và khoanh vùng với màu sắc dựa vào "muc_do"
        this.locationsList.forEach((value, index) => {
          var khoang_cach = this.mapInstance.tinhKhoangCachDenDiem(value.toa_do.lat, value.toa_do.lng);
          value.content = "Cách " + khoang_cach + "km so với vị trí hiện tại";

          // // Vẽ đường đi
          // this.mapInstance.veDuongDi(this.position.lat, this.position.lng, value.toa_do.lat, value.toa_do.lng);
          // 
          // Tạo địa điểm
          // this.mapInstance.taoDiaDiem(value.toa_do.lat, value.toa_do.lng, value.ten_vung, value.mo_ta);

          // Dựa vào mức độ để thay đổi màu sắc của vùng tròn
          this.drawCircleBasedOnLevel(value);
        });
      },
      // Hàm khoanh vùng và thay đổi màu sắc của vùng tròn dựa vào "muc_do"
      drawCircleBasedOnLevel(location) {
        let color = this.getColorByLevel(location.muc_do); // Lấy màu sắc theo mức độ
        let radius = 1000; // Bán kính của vùng tròn (500m)

        // Vẽ vùng tròn với màu sắc tương ứng
        this.mapInstance.veVungTron(location.toa_do.lat, location.toa_do.lng, radius, color);
      },
      // Hàm xác định màu sắc dựa vào mức độ
      getColorByLevel(muc_do) {
        // Màu sắc dựa vào mức độ (muc_do)
        if (muc_do <= 3) {
          return "#FF0000"; // Màu đỏ cho mức độ thấp
        } else if (muc_do <= 6) {
          return "#FFFF00"; // Màu vàng cho mức độ trung bình
        } else {
          return "#00FF00"; // Màu xanh lá cho mức độ cao
        }
      },
      // Các phương thức khác
      reset() {
        // Reset currentIndex và kích hoạt sự kiện "onReset"
        this.currentIndex = 0;
        appsmith.triggerEvent("onReset");
      },
      showPosition(position) {
        console.log(position);
      }
    }
  });
});

