appsmith.onReady(() => {
  function createVueInstance() {
    new Vue({
      el: "#app",
      data() {
        return {
          mapInstance: null,
          position: appsmith.model.map
        };
      },
      mounted() {
        this.initMap();
        appsmith.onModelChange("map", () => {
          this.updatePosition();
        });
      },
      methods: {
        initMap() {
          this.mapInstance = new DTUAppsmithMap("map", {
            center: [this.position.lat, this.position.lng],
            zoom: 16
          });

          // Cập nhật địa chỉ từ tọa độ
          this.fetchLocation();
        },
        updatePosition() {
          // Cập nhật vị trí khi "map" thay đổi
          this.position = appsmith.model.map;

          // Cập nhật bản đồ
          this.mapInstance.capNhatViTriHienTai(this.position.lat, this.position.lng, this.position.title, this.position.location);

          // Gọi API để lấy địa chỉ
          this.fetchLocation();
        },
        async fetchLocation() {
          try {
            // Gọi Nominatim API để lấy địa chỉ từ tọa độ
            const response = await fetch(`https://nominatim.openstreetmap.org/reverse?format=json&lat=${this.position.lat}&lon=${this.position.lng}&addressdetails=1`);
            const data = await response.json();

            // Lấy địa chỉ đầy đủ
            if (data && data.display_name) {
              this.position.location = data.display_name;

              // Cập nhật trên bản đồ (nếu cần)
              this.mapInstance.capNhatViTriHienTai(this.position.lat, this.position.lng, this.position.title, this.position.location);
            }
          } catch (error) {
            console.error("Lỗi khi gọi Nominatim API:", error);
          }
        }
      }
    });
