appsmith.onReady(() => {
  function createVueInstance() {
    new Vue({
      el: "#app",
      data() {
        return {
          mapInstance: null,
          position: appsmith.model.map,
          // Vị trí hiện tại
          location: appsmith.model.location // Vị trí cần hiển thị trên bản đồ
        };
      },
      mounted() {
        this.initMap();
        appsmith.onModelChange("map", () => {
          this.updatePosition();
        });
      },
      methods: {
        initMap() {
          this.mapInstance = new DTUAppsmithMap("map", {
            center: [this.location.lat, this.location.lng],
            zoom: 16
          });

          // Cập nhật vị trí hiện tại
          this.mapInstance.capNhatViTriHienTai(this.location.lat, this.location.lng, this.location.title, this.location.location);

          // Hiển thị vị trí của location trên bản đồ
          this.showLocation();

          // Vẽ đường đi từ vị trí hiện tại đến location
          this.drawRoute();
        },
        updatePosition() {
          // Cập nhật vị trí hiện tại khi "map" thay đổi
          this.position = appsmith.model.map;
          this.mapInstance.capNhatViTriHienTai(this.location.lat, this.location.lng, this.location.title, this.location.location);

          // Hiển thị lại vị trí location và vẽ lại đường đi
          this.showLocation();
          this.drawRoute();
        },
        showLocation() {
          if (this.location) {
            // Hiển thị điểm location trên bản đồ
            this.mapInstance.taoDiaDiem(this.position.lat, this.position.lng, this.position.title || "Vị trí cần đến",
            // Tiêu đề location
            this.position.content || "Thông tin về location",
            // Nội dung
            this.position.iconClass || "fas fa-map-marker-alt",
            // Icon marker
            this.position.iconColor || "#FF5733",
            // Màu icon
            {
              marker: {},
              popup: {}
            });
          }
        },
        drawRoute() {
          if (this.location && this.position) {
            // Tính toán khoảng cách và vẽ đường đi từ vị trí hiện tại đến location
            const distance = this.mapInstance.tinhKhoangCachDenDiem(this.position.lat, this.position.lng, this.position.lat, this.position.lng);

            // Cập nhật thông tin khoảng cách vào location
            this.position.content = `Cách ${distance} km so với vị trí hiện tại`;

            // Vẽ đường đi từ vị trí hiện tại đến location
            this.mapInstance.veDuongDi(this.location.lat, this.location.lng, this.position.lat, this.position.lng);
          }
        }
      }
    });
  }
  createVueInstance(); // Tạo instance khi Appsmith sẵn sàng

  // Tạo lại instance khi model thay đổi
  appsmith.onModelChange(() => {
    createVueInstance();
  });
});

